	(54, 1, '2021-10-21 11:35:49', '2021-10-21 03:35:49', '<!-- wp:group {"align":"wide"} -->\n<div class="wp-block-group alignwide"></div>\n<!-- /wp:group -->\n\n<!-- wp:columns {"align":"wide"} -->\n<div class="wp-block-columns alignwide"><!-- wp:column -->\n<div class="wp-block-column"><!-- wp:group -->\n<div class="wp-block-group"><!-- wp:heading {"level":3} -->\n<h3>工作与日子</h3>\n<!-- /wp:heading -->\n\n<!-- wp:image {"align":"center","id":66,"width":1339,"height":474,"sizeSlug":"full","linkDestination":"none"} -->\n<div class="wp-block-image"><figure class="aligncenter size-full is-resized"><img src="http://localhost/wordpress/wp-content/uploads/2021/10/banner-background.png" alt="" class="wp-image-66" width="1339" height="474"/></figure></div>\n<!-- /wp:image -->\n\n<!-- wp:paragraph -->\n<p>8月1日—12月1日</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:button {"className":"is-style-outline"} -->\n<div class="wp-block-button is-style-outline"><a class="wp-block-button__link" href="https://make.wordpress.org/core/2019/09/27/block-editor-theme-related-updates-in-wordpress-5-3/">阅读更多</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:group -->\n\n<!-- wp:group -->\n<div class="wp-block-group"><!-- wp:image {"align":"full","id":37,"sizeSlug":"full"} -->\n<figure class="wp-block-image alignfull size-full"><img src="http://localhost/wordpress/wp-content/themes/twentytwenty/assets/images/2020-three-quarters-3.png" alt="" class="wp-image-37"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {"level":3} -->\n<h3>战区</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>10月1日—12月1日</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:button {"className":"is-style-outline"} -->\n<div class="wp-block-button is-style-outline"><a class="wp-block-button__link" href="https://make.wordpress.org/core/2019/09/27/block-editor-theme-related-updates-in-wordpress-5-3/">阅读更多</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:group --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class="wp-block-column"><!-- wp:group -->\n<div class="wp-block-group"><!-- wp:image {"align":"full","id":37,"sizeSlug":"full"} -->\n<figure class="wp-block-image alignfull size-full"><img src="http://localhost/wordpress/wp-content/themes/twentytwenty/assets/images/2020-three-quarters-2.png" alt="" class="wp-image-37"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {"level":3} -->\n<h3>我应得的生活</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>8月1日—12月1日</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:button {"className":"is-style-outline"} -->\n<div class="wp-block-button is-style-outline"><a class="wp-block-button__link" href="https://make.wordpress.org/core/2019/09/27/block-editor-theme-related-updates-in-wordpress-5-3/">阅读更多</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:group -->\n\n<!-- wp:group -->\n<div class="wp-block-group"><!-- wp:image {"align":"full","id":37,"sizeSlug":"full"} -->\n<figure class="wp-block-image alignfull size-full"><img src="http://localhost/wordpress/wp-content/themes/twentytwenty/assets/images/2020-three-quarters-4.png" alt="" class="wp-image-37"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:heading {"level":3} -->\n<h3>从希涅克到马蒂斯</h3>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph -->\n<p>10月1日—12月1日</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:button {"className":"is-style-outline"} -->\n<div class="wp-block-button is-style-outline"><a class="wp-block-button__link" href="https://make.wordpress.org/core/2019/09/27/block-editor-theme-related-updates-in-wordpress-5-3/">阅读更多</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:group --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:image {"align":"full","id":37,"sizeSlug":"full"} -->\n<figure class="wp-block-image alignfull size-full"><img src="http://localhost/wordpress/wp-content/themes/twentytwenty/assets/images/2020-landscape-2.png" alt="" class="wp-image-37"/></figure>\n<!-- /wp:image -->\n\n<!-- wp:group {"align":"wide"} -->\n<div class="wp-block-group alignwide"><!-- wp:heading {"textAlign":"center","textColor":"accent"} -->\n<h2 class="has-text-align-center has-accent-color has-text-color">“赛博格”，正如哲学家唐娜·哈拉维所言，“不具备恭敬之心。他们没有记住宇宙。”</h2>\n<!-- /wp:heading --></div>\n<!-- /wp:group -->\n\n<!-- wp:paragraph {"dropCap":true} -->\n<p class="has-drop-cap">UMoMA共有七层引人注目的建筑，可举办国际当代艺术展览，有时还包括艺术史回顾展。存在性、政治性和哲学性的问题是我们展览的固有内容。作为访客，您可以免费参观艺术家的导览、讲座、电影放映和其他活动</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>这些展览是由UMoMA与世界各地的艺术家和博物馆合作制作的，它们经常引起国际关注。UMoMA已获得欧洲年度博物馆的特别表彰，并且是瑞典年度博物馆奖和欧洲理事会博物馆奖的最佳候选人之一。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:group {"align":"wide","style":{"color":{"background":"#ffffff"}}} -->\n<div class="wp-block-group alignwide has-background" style="background-color:#ffffff"><!-- wp:group -->\n<div class="wp-block-group"><!-- wp:heading {"textAlign":"center"} -->\n<h2 class="has-text-align-center">成为会员并获得独家优惠！</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {"align":"center"} -->\n<p class="has-text-align-center">会员可以参观独家展览和销售。我们的会员费为$99.99，按年收费。</p>\n<!-- /wp:paragraph -->\n\n<!-- wp:button {"align":"center"} -->\n<div class="wp-block-button aligncenter"><a class="wp-block-button__link" href="https://make.wordpress.org/core/2019/09/27/block-editor-theme-related-updates-in-wordpress-5-3/">加入我们</a></div>\n<!-- /wp:button --></div>\n<!-- /wp:group --></div>\n<!-- /wp:group -->\n\n<!-- wp:gallery {"ids":[39,38],"linkTo":"none","align":"wide"} -->\n<figure class="wp-block-gallery alignwide columns-2 is-cropped"><ul class="blocks-gallery-grid"><li class="blocks-gallery-item"><figure><img src="http://localhost/wordpress/wp-content/themes/twentytwenty/assets/images/2020-square-2.png" alt="" data-id="39" data-full-url="http://localhost/wordpress/wp-content/themes/twentytwenty/assets/images/2020-square-2.png" data-link="assets/images/2020-square-2/" class="wp-image-39"/></figure></li><li class="blocks-gallery-item"><figure><img src="http://localhost/wordpress/wp-content/themes/twentytwenty/assets/images/2020-square-1.png